/// \file
/// This file declares the targeting of the Machinelegalizer class for Primate.
/// \todo This should be generated by TableGen.
//===----------------------------------------------------------------------===//

#ifndef LLVM_LIB_TARGET_PRIMATE_PRIMATEMACHINELEGALIZER_H
#define LLVM_LIB_TARGET_PRIMATE_PRIMATEMACHINELEGALIZER_H

#include "llvm/CodeGen/GlobalISel/LegalizerInfo.h"

#define DEBUG_TYPE "Primate-Legalizer-Info"

namespace llvm {

class PrimateSubtarget;

/// This class provides the information for the target register banks.
class PrimateLegalizerInfo : public LegalizerInfo {
public:
  PrimateLegalizerInfo(const PrimateSubtarget &ST);
  virtual bool legalizeIntrinsic(LegalizerHelper &Helper,
                                 MachineInstr &MI) const override {
    LLVM_DEBUG(dbgs() << "Attempting to legalize intrinsic: ");
    LLVM_DEBUG(MI.dump());
    return true;
  }
};
} // end namespace llvm
#endif
